package flowControlStatement.patternStmt;

public class Program4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stubfor(int i=1;i<=3;i++) {
		int n=3;
	for(int i=1;i<=n;i++) {
		for(int j=1;j<=n;j++) {
		if(i+j==n+1){
		System.out.println("*");}
		else
		System.out.println(" ");
		}
		System.out.println();
		

	}

}
}
